-- Drop the database if it exists to start fresh
DROP DATABASE IF EXISTS seafood_restaurant;
-- Create the database
CREATE DATABASE seafood_restaurant;
-- Select the database to use
USE seafood_restaurant;

-- Create the role table
CREATE TABLE `role` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(100) NOT NULL UNIQUE
);

-- Create the user table
CREATE TABLE `user` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL,
    `email` VARCHAR(255) UNIQUE NOT NULL,
    `password` VARCHAR(255) NOT NULL, -- In a real application, store hashed passwords
    `phone` VARCHAR(20) UNIQUE,
    `role_id` BIGINT,
    `is_active` BIT NOT NULL DEFAULT 1,
    `last_login` DATETIME NULL DEFAULT NULL,
    `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (`role_id`) REFERENCES `role`(`id`) ON DELETE SET NULL
);

-- Create the category table
CREATE TABLE `category` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL UNIQUE
);

-- Create the dish table (price is BIGINT)
CREATE TABLE `dish` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL,
    `description` TEXT,
    `price` BIGINT NOT NULL CHECK (`price` >= 0), -- Uses BIGINT
    `category_id` BIGINT,
    `image` VARCHAR(255),
    `able` BIT NOT NULL DEFAULT 1, -- Availability status
    FOREIGN KEY (`category_id`) REFERENCES `category`(`id`) ON DELETE SET NULL
);

-- Create the shift table
CREATE TABLE `shift` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `time_start` DATETIME NOT NULL,
    `time_end` DATETIME NOT NULL
);

-- Create the table (restaurant table) table
CREATE TABLE `table` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `table_number` INT NOT NULL UNIQUE,
    `curr_order_session_id` BIGINT NULL DEFAULT NULL UNIQUE
);

-- Create the order_session table (total_price is BIGINT, is_paid REMOVED)
CREATE TABLE `order_session` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `shift_id` BIGINT,
    `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    `total_price` BIGINT NOT NULL DEFAULT 0 CHECK (`total_price` >= 0), -- Uses BIGINT
    -- `is_paid` BIT NOT NULL DEFAULT 0, -- REMOVED
    `status` VARCHAR(50) DEFAULT 'Ordered', -- 'Ordered', 'In Progress', 'Ready To Pay', 'Completed'
    `payment_time` DATETIME NULL DEFAULT NULL, -- Payment time implies paid status
    `table_id` BIGINT NOT NULL,
    FOREIGN KEY (`shift_id`) REFERENCES `shift`(`id`) ON DELETE SET NULL,
    FOREIGN KEY (`table_id`) REFERENCES `table`(`id`) 
);


-- Create the order table
CREATE TABLE `order` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `order_session_id` BIGINT NOT NULL,
    `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
	`note` LONGTEXT,
    FOREIGN KEY (`order_session_id`) REFERENCES `order_session`(`id`) ON DELETE CASCADE
);

-- Create the order_log table
CREATE TABLE `order_log` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `order_id` BIGINT NULL,
    `message` VARCHAR(255),
    `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (`order_id`) REFERENCES `order`(`id`) ON DELETE CASCADE
);


-- Create the order_detail table (price is BIGINT, no 'Cancelled' generated)
CREATE TABLE `order_detail` (
    `id` BIGINT PRIMARY KEY AUTO_INCREMENT,
    `order_id` BIGINT NOT NULL,
    `dish_id` BIGINT NOT NULL,
    `quantity` INT NOT NULL CHECK (`quantity` > 0),
    `price` BIGINT NOT NULL, -- Uses BIGINT
    `status` VARCHAR(50) DEFAULT 'Ordered', -- e.g., Ordered, Cooking, Finished
    FOREIGN KEY (`order_id`) REFERENCES `order`(`id`) ON DELETE CASCADE,
    FOREIGN KEY (`dish_id`) REFERENCES `dish`(`id`) ON DELETE RESTRICT
);

